import React from "react";
import "./Sidebar.css";
import { Link } from "react-router-dom";

const Sidebar = () => {
  const menuClick = (event) => {
    var menu = document.getElementsByClassName("sidebar-title");
    var index = document.getElementsByClassName("sidebar-content");
    var content = document.getElementsByClassName("sidebar-index-content");
    console.log(content.length);
    for (var i = 0; i < menu.length; i++) {
      if (menu[i] === event.target) {
        if (index[i].style.display == "block") {
          index[i].style.display = "none";
          for (var j = 0; j < content.length; j++) {
            content[j].style.display = "none";
          }
        } else {
          index[i].style.display = "block";
        }
      }
    }
  };

  const indexOnClick = (event) => {
    var index = document.getElementsByClassName("sidebar-index");
    var content = document.getElementsByClassName("sidebar-index-content");

    for (var i = 0; i < index.length; i++) {
      if (index[i] === event.target) {
        if (content[i].style.display == "block") {
          content[i].style.display = "none";
        } else {
          content[i].style.display = "block";
        }
      }
    }
  };
  return (
    <ul class="sidebar">
      <li class="sidebar-item">
        <span class="sidebar-title" onClick={menuClick}>
          자바스크립트 & node.js
        </span>
        <ul class="sidebar-content">
          <li class="sidebar-index" onClick={indexOnClick}>
            0. 시작하기 앞서
          </li>
          <ul class="sidebar-index-content">
            <Link to="" className="sidebar-index-content-link">
              0-1. 자바스크립트란?
            </Link>
            <Link to="" className="sidebar-index-content-link">
              0-2. 자바스크립트의 특징
            </Link>
            <Link to="" className="sidebar-index-content-link">
              0-3. 자바스크립트 엔진이란?
            </Link>
          </ul>
          <li class="sidebar-index" onClick={indexOnClick}>
            1. 자바스크립트 기초
          </li>
          <ul class="sidebar-index-content">
            <a>1-1. 기본 문법</a>
            <a>1-2. 연산자</a>
            <a>1-3. 데이터 타입</a>
            <a>1-4. 조건문</a>
            <a>1-5. 반복문</a>
            <a>1-6. 함수</a>
          </ul>
          <li class="sidebar-index" onClick={indexOnClick}>
            2. 객체지향 자바스크립트
          </li>
          <ul class="sidebar-index-content">
            <a>2-1. 클래스</a>
            <a>2-2. 생성자</a>
            <a>2-3. 캡슐화</a>
            <a>2-4. 집합</a>
            <a>2-5. 상속</a>
            <a>2-6. 스코프와 호이스팅</a>
            <a>2-7. 클로저</a>
          </ul>
          <li class="sidebar-index" onClick={indexOnClick}>
            3. Node.js 소개
          </li>
          <ul class="sidebar-index-content">
            <li>3-1. 개요</li>
            <li>3-2. 이벤트 기반 비동기 방식</li>
            <li>3-3. 그 외 특징들</li>
            <li>3-4. 아키텍처</li>
          </ul>
          <li class="">4. Node.js 설치</li>

          <li class="sidebar-index" onClick={indexOnClick}>
            5. Node.js 간단한 실습
          </li>
          <ul class="sidebar-index-content">
            <li>5-1. 웹 서버</li>
            <li>5-2. 파일 입출력</li>
            <li>5-2. 비동기 이벤트</li>
          </ul>
          <li class="sidebar-index" onClick={indexOnClick}>
            6. Node.js 기본 모듈과 기초
          </li>
          <ul class="sidebar-index-content">
            <li>6-1. 노드의 모듈화 개념</li>
            <li>6-2. 전역 객체</li>
            <li>6-3. 모듈의 종류</li>
            <li>6-4. 기본 모듈들 사용법</li>
          </ul>
        </ul>
      </li>
      <li class="sidebar-item"></li>
      <li class="sidebar-item">
        <a href="">1</a>
      </li>
      <li class="sidebar-item">
        <a href="">1</a>
      </li>
      <li class="sidebar-item">
        <a href="">1</a>
      </li>
      <li class="sidebar-item">
        <a href="">1</a>
      </li>
      <li class="sidebar-item">
        <a href="">1</a>
      </li>
      <li class="sidebar-item">
        <a href="">1</a>
      </li>
      <li class="sidebar-item">
        <a href="">1</a>
      </li>
    </ul>
  );
};

export default Sidebar;
